import { CommonModule } from '@angular/common';
import { Component, input, output } from '@angular/core';
import { Product } from '../../models/product.model';

@Component({
  selector: 'app-product-item',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './product-item.component.html',
  styleUrl: './product-item.component.css',
})
export class ProductItemComponent {
  product = input.required<Product>();
  delete = output<number>();

  like(): void {
    const current = this.product();
    if (!current) {
      return;
    }
    current.likes += 1;
  }

  onDelete(): void {
    const current = this.product();
    if (!current) {
      return;
    }
    this.delete.emit(current.id);
  }

  get whatsAppShareUrl(): string {
    const current = this.product();
    const text = `Check out this product: ${current.link}`;
    return `https://wa.me/?text=${encodeURIComponent(text)}`;
  }

  get telegramShareUrl(): string {
    const current = this.product();
    const url = current.link;
    const text = current.name;
    return `https://t.me/share/url?url=${encodeURIComponent(
      url
    )}&text=${encodeURIComponent(text)}`;
  }
}

<article class="product-card">
  <div class="product-card__image-wrapper">
    <img
      class="product-card__image"
      [src]="product().image"
      [alt]="product().name"
      loading="lazy"
    />
  </div>

  <div class="product-card__body">
    <h2 class="product-card__title">
      <a
        [href]="product().link"
        target="_blank"
        rel="noopener noreferrer"
      >
        {{ product().name }}
      </a>
    </h2>

    <div class="product-card__rating-row">
      <span class="product-card__rating-stars">★ {{ product().rating | number : '1.1-1' }}</span>
      <span class="product-card__likes-text">❤ {{ product().likes }}</span>
    </div>

    <div class="product-card__price-row">
      <div class="product-card__price-main">
        <span class="product-card__price-label">Цена</span>
        <span class="product-card__price-value">
          {{ product().price | number : '1.0-0' }} ₸
        </span>
      </div>
      <div class="product-card__installment">
        В рассрочку
      </div>
    </div>

    <div class="product-card__actions">
      <a
        class="btn btn--kaspi-outline"
        [href]="product().link"
        target="_blank"
        rel="noopener noreferrer"
      >
        Открыть на Kaspi
      </a>

      <button
        type="button"
        class="btn btn--primary"
        (click)="like()"
      >
        Лайк
      </button>

      <button
        type="button"
        class="btn btn--danger"
        (click)="onDelete()"
      >
        Удалить
      </button>
    </div>

    <div class="product-card__share-group">
      <a
        class="btn btn--ghost"
        [href]="whatsAppShareUrl"
        target="_blank"
        rel="noopener noreferrer"
      >
        WhatsApp
      </a>
      <a
        class="btn btn--ghost"
        [href]="telegramShareUrl"
        target="_blank"
        rel="noopener noreferrer"
      >
        Telegram
      </a>
    </div>
  </div>
</article>
