import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ProductListComponent } from './components/product-list/product-list.component';
import { ProductService } from './services/product.service';
import { Category } from './models/category.model';
import { Product } from './models/product.model';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, ProductListComponent],
  templateUrl: './app.component.html',
  styleUrl: './app.component.css',
})
export class AppComponent {
  title = 'Online Store';

  categories: Category[] = [];
  selectedCategoryId: number | null = null;
  selectedProducts: Product[] = [];

  constructor(private readonly productService: ProductService) {
    this.categories = this.productService.getCategories();
  }

  selectCategory(categoryId: number): void {
    this.selectedCategoryId = categoryId;
    this.selectedProducts =
      this.productService.getProductsByCategory(categoryId);
  }
}

<header class="app-header">
  <div class="app-header__inner">
    <div class="app-header__brand">
      <span class="app-header__logo">üõçÔ∏è</span>
      <span class="app-header__title">{{ title }}</span>
    </div>
  </div>
</header>

<main class="app-main app-layout">
  <aside class="app-sidebar">
    <h2 class="app-sidebar__title">Categories</h2>
    <div class="app-sidebar__list">
      @for (category of categories; track category.id) {
        <button
          type="button"
          class="app-sidebar__item"
          [class.app-sidebar__item--active]="category.id === selectedCategoryId"
          (click)="selectCategory(category.id)"
        >
          {{ category.name }}
        </button>
      }
    </div>
  </aside>

  <section class="app-content">
    @if (!selectedCategoryId) {
      <section class="app-welcome">
        <h1 class="app-welcome__title">Welcome to the Online Store</h1>
        <p class="app-welcome__text">
