import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Product } from '../../models/product.model';
import { ProductItemComponent } from '../product-item/product-item.component';

@Component({
  selector: 'app-product-list',
  standalone: true,
  imports: [CommonModule, ProductItemComponent],
  templateUrl: './product-list.component.html',
  styleUrl: './product-list.component.css',
})
export class ProductListComponent implements OnChanges {
  @Input() products: Product[] = [];

  /**
   * Internal copy of products used for rendering and deletions.
   * When the input changes (category switched), this array is reset.
   */
  visibleProducts: Product[] = [];

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['products']) {
      this.visibleProducts = [...(this.products ?? [])];
    }
  }

  handleDelete(productId: number): void {
    this.visibleProducts = this.visibleProducts.filter(
      (product) => product.id !== productId
    );
  }
}

<section class="products">
  <header class="products__header">
    <div>
      <h2 class="products__title">Products</h2>
      <p class="products__subtitle">
        Категориядағы тауарлар тізімі. Ұнаған өнімдерді лайктап, қажет
        болмаса өшіре аласыз.
      </p>
    </div>
  </header>

  @if (visibleProducts.length) {
    <div class="products__grid">
      @for (product of visibleProducts; track product.id) {
        <app-product-item
          [product]="product"
          (delete)="handleDelete($event)"
        ></app-product-item>
      }
    </div>
  } @else {
    <p class="products__empty">Бұл категорияда өнімдер жоқ.</p>
  }
</section>
